import React, { useState, useEffect } from "react";
import { call } from "../service/api-service";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

export default function Setting() {

    const [settings, setSettings] = useState([]); //캐릭터 설정값
    useEffect(() => {
        call("/member/settings", "GET", null)
            .then((response) => {
                setSettings(response);
            })
            .catch((error) => {
                alert(error.errorMessage);
            });
    }, []);

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>id</TableCell>
                        <TableCell align="right">서버</TableCell>
                        <TableCell align="right">캐릭터 이름</TableCell>
                        <TableCell align="right">클래스</TableCell>
                        <TableCell align="right">아이템레벨</TableCell>
                        <TableCell align="right">캐릭터 출력</TableCell>
                        <TableCell align="right">출석&에포나 출력</TableCell>
                        <TableCell align="right">카오스던전 출력</TableCell>
                        <TableCell align="right">가디언토벌 출력</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {settings.map((setting) => (
                        <TableRow
                            key={setting.id}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell component="th" scope="row">
                                {setting.id}
                            </TableCell>
                            <TableCell align="right">{setting.serverName}</TableCell>
                            <TableCell align="right">{setting.characterName}</TableCell>
                            <TableCell align="right">{setting.characterClassName}</TableCell>
                            <TableCell align="right">{setting.itemLevel}</TableCell>
                            <TableCell align="right">{setting.showCharacter ? "true" : "false"}</TableCell>
                            <TableCell align="right">{setting.showEpona ? "true" : "false"}</TableCell>
                            <TableCell align="right">{setting.showChaos ? "true" : "false"}</TableCell>
                            <TableCell align="right">{setting.showGuardian ? "true" : "false"}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}
