import React, { useState } from "react";
import { Container, Grid, Typography, TextField, Button } from "@mui/material";
import { signup } from "../service/api-service";
import { Link } from "react-router-dom";
import './Todo.css';

function SignUp() {
  const handleSubmit = (event) => {
    event.preventDefault();
    // 오브젝트에서 form에 저장된 데이터를 맵의 형태로 바꿔줌.
    const data = new FormData(event.target);
    const username = data.get("username");
    const password = data.get("password");
    const apiKey = data.get("apiKey");
    const characterName = data.get("characterName");
    setNotification("잠시만 기다려주세요");
    signup({
      username: username, password: password,
      apiKey: apiKey, characterName: characterName
    })
      .then((response) => {
        setNotification(null);
        alert("가입이 완료되었습니다.");
        // 계정 생성 성공 시 login페이지로 리디렉트
        window.location.href = "/login";
      })
      .catch((error) => {
        alert(error.errorMessage);
      });
  };
  const [notification, setNotification] = useState(null); // 상태 추가

  return (

    <>
      <Container component="main" maxWidth="xs" style={{ marginTop: "8%" }}>
        <form noValidate onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography component="h1" variant="h5">
                계정 생성
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <TextField
                autoComplete="fname"
                name="username"
                variant="outlined"
                required
                fullWidth
                id="username"
                label="아이디"
                autoFocus
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="패스워드"
                type="password"
                id="password"
                autoComplete="current-password"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="apiKey"
                variant="outlined"
                required
                fullWidth
                id="apiKey"
                label="로스트아크 ApiKey"
              />
              <Link to="https://canfactory.tistory.com/1081" target="_blank" variant="body2">
                apikey발급을 모르십니까?
              </Link>
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="characterName"
                variant="outlined"
                required
                fullWidth
                id="characterName"
                label="대표캐릭터"
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
              >
                계정 생성
              </Button>
            </Grid>
            <Grid item xs={12}>
              <Link to="/login" variant="body2">
                이미 계정이 있습니까? 로그인 하세요.
              </Link>
            </Grid>
          </Grid>
        </form>
      </Container>
      <div
        className={`notification ${notification ? "show" : ""}`}
      >
        {notification}
      </div>
    </>
  );
};

export default SignUp;
