import React, { useState, useEffect } from "react";
import { call } from "../service/api-service";
import { DataGrid } from '@mui/x-data-grid';

const columns = [
    { field: 'id', headerName: 'ID', width: 90 },
    { field: 'serverName', headerName: '서버', width: 130 },
    { field: 'characterName', headerName: '캐릭터 이름', width: 130 },
    { field: 'characterClassName', headerName: '클래스', width: 130 },
    { field: 'itemLevel', headerName: '아이템 레벨', width: 130 },
    { field: 'showCharacter', headerName: '캐릭터 출력', width: 130 },
    { field: 'showEpona', headerName: '출석&에포나', width: 130 },
    { field: 'showChaos', headerName: '카오스던전', width: 130 },
    { field: 'showGuardian', headerName: '가디언토벌', width: 130 },
];

export default function Setting() {

    const [settings, setSettings] = useState([]); //캐릭터 설정값
    useEffect(() => {
        call("/member/settings", "GET", null)
            .then((response) => {
                setSettings(response);
            })
            .catch((error) => {
                alert(error.errorMessage);
            });
    }, []);
    return (
        <div style={{ height: 600, width: '100%' }}>
            <DataGrid
                rows={settings}
                columns={columns}
                initialState={{
                    pagination: {
                        paginationModel: { page: 0, pageSize: 10 },
                    },
                }}
                pageSizeOptions={[10, 15, 20, 25, 30]}
                checkboxSelection
            />
        </div>
    );
}
