import React, { useState } from 'react';
import { call } from "../service/api-service";
import SpeedDial from '@mui/material/SpeedDial';
import SpeedDialIcon from '@mui/material/SpeedDialIcon';
import SpeedDialAction from '@mui/material/SpeedDialAction';
import SettingsIcon from '@mui/icons-material/Settings';
import DownloadIcon from '@mui/icons-material/Download';

export default function BasicSpeedDial(props) {
    const [characters, setCharacters] = useState([...props.characters]);
    const [linearProgress, setLinearProgress] = useState([...props.linearProgress]);
    const icons = [
        { icon: <SettingsIcon />, name: '출력 내용 변경' },
        { icon: <DownloadIcon />, name: '캐릭터 정보 업데이트' },
    ];

    const actions = (name) => {
        if (name === "출력 내용 변경") {
            window.location.href = "setting";
        }
        if (name === "캐릭터 정보 업데이트") {
            setLinearProgress(true);

            call("/member/characterList", "PATCH", null)
                .then((response) => {
                    setLinearProgress(false);
                    props.showMessage("정보 업데이트가 완료되었습니다.");
                    setCharacters(response);
                })
        }
    }
    return (
        <SpeedDial
            ariaLabel="SpeedDial basic example"
            sx={{ position: 'fixed', bottom: 30, right: 30 }}
            icon={<SpeedDialIcon />}
        >
            {icons.map((icon) => (
                <SpeedDialAction
                    key={icon.name}
                    icon={icon.icon}
                    tooltipTitle={icon.name}
                    onClick={() => actions(icon.name)}
                />
            ))}
        </SpeedDial>
    );
}
