import { useState, useEffect } from "react";
import { call } from "../service/api-service";
import './Comments.css';
import Comment from "./Commnet";

const Comments = () => {
    const [backendComments, setBackendComments] = useState([]);
    console.log("backendCommnets", backendComments);
    const rootComments = backendComments.filter(
        (backendComment) => backendComment.parentId === 0
      );

    useEffect(() => {
        // 초기 캐릭터 정보 불러오기
        call("/comments", "GET", null)
            .then((response) => {
                setBackendComments(response);
            })
            .catch((error) => { console.log(error.errorMessage)});
    }, []);


    return (
        <div className="comments">
            <h3 className="comments-title">Comments</h3>
            <div className="comment-form-title">Write comment</div>
            {/* <CommentForm submitLabel="Write" handleSubmit={} /> */}
            <div className="comments-container">
                {rootComments.map((rootComment) => (
                    <Comment key={rootComment.id} comment={rootComment} />
                ))}

            </div>
        </div>
    );
};

export default Comments;
